import { Component } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FaIconLibrary } from '@fortawesome/angular-fontawesome';
import { fas } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-course-form',
  templateUrl: './course-form.component.html',
  styleUrls: ['./course-form.component.scss'],
})
export class CourseFormComponent {
  courseForm: FormGroup;

  constructor(public fb: FormBuilder, public library: FaIconLibrary) {
    library.addIconPacks(fas);

    this.courseForm = this.fb.group({
      title: ['', Validators.required],
      description: ['', Validators.required],
      author: [''],
      authors: this.fb.array([]),
      duration: [null, Validators.required]
    });
  }

  get authors(): FormArray {
    return this.courseForm.get('authors') as FormArray;
  }

  addAuthor() {
    const value = this.courseForm.get('author')?.value;
    if (value && value.trim()) {
      this.authors.push(this.fb.control(value.trim()));
      this.courseForm.get('author')?.setValue('');
    }
  }

  removeAuthor(index: number) {
    this.authors.removeAt(index);
  }

  submit() {
    if (this.courseForm.valid) {
      // 
    }
  }
}
<h2>Create / Edit Course</h2>
<form [formGroup]="courseForm" (ngSubmit)="submit()">
  <div class="app-plate">
    <div class="course">
      <h3>Main Info</h3>
      <div class="form__control">
        <label for="title">Title</label>
        <input type="text" id="title" formControlName="title" placeholder="Input text"/>
        <span id="titleErrorMessage" class="text-danger" *ngIf="courseForm.get('title')?.invalid && courseForm.get('title')?.touched">
          Title is required.
        </span>
      </div>
      <div class="form__control">
        <label for="description">Description</label>
        <textarea id="description" formControlName="description" placeholder="Input text" rows="4" cols="5"></textarea>
        <span id="descriptionErrorMessage" class="text-danger" *ngIf="courseForm.get('description')?.invalid && courseForm.get('description')?.touched">
          Description is required.
        </span>
      </div>
      <div class="separator"></div>
      <h3>Authors</h3>
      <div class="form__control">
        <div class="course__flex">
          <input type="text" id="author" formControlName="author" placeholder="Input Author Name"/>
          <app-button (clicked)="addAuthor()" [buttonText]="'Create'"></app-button>
        </div>
        <span id="authorErrorMessage">New author should contain only latin letters and numbers.</span>
        <div class="course__authors">
          <div class="course__authors-item" *ngFor="let a of authors.controls; let i = index">
            {{ a.value }}
            <span class="course__icon" (click)="removeAuthor(i)"><fa-icon [icon]="'trash'"></fa-icon></span>
          </div>
        </div>
      </div>
      <div class="separator"></div>
      <h3>Duration</h3>
      <div class="form__control">
        <div class="course__flex">
          <input type="number" id="duration" formControlName="duration" placeholder="Input duration"/>
          <div class="course__duration">
            {{ courseForm.get('duration')?.value }}
          </div>
        </div>
        <span id="durationErrorMessage" class="text-danger" *ngIf="courseForm.get('duration')?.invalid && courseForm.get('duration')?.touched">
          Duration is required.
        </span>
      </div>
      <div class="form__action">
        <app-button [buttonText]="'Save'"></app-button>
      </div>
    </div>
  </div>
</form>
