import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FaIconLibrary } from '@fortawesome/angular-fontawesome';
import { fas } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.scss']
})
export class ModalComponent {
  constructor(library: FaIconLibrary) {
    library.addIconPacks(fas);
  }

  @Input() title?: string;
  @Input() message?: string;
  @Input() okButtonText?: string;
  @Input() cancelButtonText?: string;

  @Output() ok = new EventEmitter<void>();
  @Output() cancel = new EventEmitter<void>();

  onOk() { this.ok.emit(); }
  onCancel() { this.cancel.emit(); }
}
<div class="modal__backdrop">
    <div class="modal__container">
        <div class="modal__title">
            <div>{{ title }}</div>
            <div class="modal__icon" (click)="onCancel()">
                <fa-icon [icon]="'xmark'"></fa-icon>
            </div>
        </div>
        <div class="modal__message">{{ message }}</div>
        <div class="modal__actions">
            <div class="modal__btn">
                <app-button [buttonText]="cancelButtonText || 'Cancel'" (clicked)="onCancel()"></app-button>
            </div>
            <div class="modal__btn">
                <app-button [buttonText]="okButtonText || 'Ok'" (clicked)="onOk()"></app-button>
            </div>
        </div>
    </div>
</div>
